import React, { useEffect, useState } from 'react';
import Dashboard from "../components/Dashboard";
import EventNav from "../components/EventNav";
import Stack from '@mui/material/Stack';
// import emails from "../data/emails.json"
import axios from 'axios';
import { eventsContext } from '../components/EventsContext';

export default function Home() {
    let events = [];
    const [response, setResponse] = useState(null);
    const loadResponse = (prompt) => {
        axios
        .post("/chat", { prompt })
        .then((res) => {
            console.log(res.data[0]);
        })
        .catch((err) => {
            console.error(err);
        });
    };

    let prompt = `Return only a valid JSON array of objects for this email 
    'Hi All, The Emerging Technology Group (ETG) will be hosting an in-person study group with a member of the Treasury Department and Council on Foreign Investment in the US (CFIUS) next week in Harvard Hall 202 from 5:15-6:15 PM. Free food will be provided! The talk will focus on the present environment for technology investment to and from the United States, focusing specifically on foreign direct investment (FDI) and outbound investments.'
    following this format:

    [{“event”: "a boolean of whether or not it is promoting an event with a date and location”,
    “date”: “Date first and then Time of event”,
    “location”: “the location of the event”,
    “summary”: “1- 2 sentence summary of the event”,
    “category”: “the best matching category from educational, fun, or extracurricular”]`

    if (response == null) {
        loadResponse(prompt);
    }
    if (response != undefined) {
        console.log(response)
    }


    // for (var prop in emails) {
    //     // Email hasn't been previously recorded 
    //     if (events.find((elt) => elt.id == prop) == undefined) {
    //         // Parse from AI
    //         let date = '';
    //         let location = '';
    //         let category = '';
    //         let summary = '';
    //         events.push({
    //             id: prop,
    //             subject: emails[prop][0].subject,
    //             date: date,
    //             location: location,
    //             body: emails[prop][0].text,
    //             summary: summary,
    //             category: category,
    //             removed: false,
    //             favorited: false
    //         });
    //     }
    // }

    // console.log(events)

    return (
        <div>
        {/* <NavBar/> */}
        <eventsContext.Provider
            value={[response, setResponse]}>
        <Stack direction="row" spacing={40}>
        <EventNav/>
        <Dashboard/>
        </Stack>
        </eventsContext.Provider>
        </div>
    );
}
