import React, { useEffect, useState } from 'react';
import Dashboard from "../components/Dashboard";
import EventNav from "../components/EventNav";
import Stack from '@mui/material/Stack';
// import emails from "../data/emails.json"
import axios from 'axios';
import { eventsContext } from '../components/EventsContext';

export default function Home() {
    let events = [];
    const [response, setResponse] = useState(null);
    const loadResponse = (prompt) => {
        axios
        .post("/chat", { prompt })
        .then((res) => {
            events.push(res.data[0]);
            setResponse(events);
        })
        .catch((err) => {
            console.error(err);
        });
    };

    let prompt = `Return just a valid JSON array of objects for this email 
    'Dear Lowell,

    The Harvard Film Archive's student group is hosting one last screening this semester. This last film comes from Brazil and is probably one you'll never be able to see elsewhere. Here are some more details + a synopsis. Hope to see you there (it's a great way to try out one of Harvard's greatest free-for-students resources, the Film Archive)!

    --

    Dear Cinematheque enthusiasts,

    This Saturday, May 6th at 7pm, come watch the Brazilian film Fogaréu (2022) at the Harvard Film Archive (located in the basement of the Carpenter Center). It's a remarkable debut film that you likely won't see anywhere else! Tickets are free with HUID and can be reserved here. This will be the last Student Cinematheque screening of the year.

    Synopsis: On the frontier between the real and the fantastic, the colonial times and the overwhelming modernity of agribusiness, the town of Goiás is the stage where the young Fernanda is faced with secrets about her past. Following the death of her foster mother, she goes back to her wealthy uncle's house to implode certainties and let the painful truth about her origins surface. (From the distributor.)'
    following this format:

    [{“event”: "a boolean of whether or not it is promoting an event with a date and location”,
    "name":"Very short title for event",
    “date”: “Numeric Month/Day/Year of event”,
    "time": "Time of event",
    “location”: “the location of the event”,
    “summary”: “1- 2 sentence summary of the event”,
    “category”: “the best matching category from educational, parties, clubs, or other”]`

    if (response == null) {
        loadResponse(prompt);
    }
    if (response != undefined) {
        console.log(response)
    }


    // for (var prop in emails) {
    //     // Email hasn't been previously recorded 
    //     if (events.find((elt) => elt.id == prop) == undefined) {
    //         // Parse from AI
    //         let date = '';
    //         let location = '';
    //         let category = '';
    //         let summary = '';
    //         events.push({
    //             id: prop,
    //             subject: emails[prop][0].subject,
    //             date: date,
    //             location: location,
    //             body: emails[prop][0].text,
    //             summary: summary,
    //             category: category,
    //             removed: false,
    //             favorited: false
    //         });
    //     }
    // }

    // console.log(events)

    return (
        <div>
        {/* <NavBar/> */}
        <eventsContext.Provider
            value={[response, setResponse]}>
        <Stack direction="row" spacing={40}>
        <EventNav/>
        <Dashboard/>
        </Stack>
        </eventsContext.Provider>
        </div>
    );
}
