    //         // Parse from AI
    //         let date = '';
    //         let location = '';
    //         let category = '';
    //         let summary = '';
    //         events.push({
    //             id: prop,
    //             subject: emails[prop][0].subject,
    //             date: date,
    //             location: location,
    //             body: emails[prop][0].text,
    //             summary: summary,
    //             category: category,
    //             removed: false,
    //             favorited: false
    //         });
    //     }
    // }

    // console.log(events)

    return (
        <div>
        {/* <NavBar/> */}
        <eventsContext.Provider
            value={[response, setResponse]}>
        <Stack direction="row" spacing={40}>
        <EventNav/>
        <Dashboard/>
        </Stack>
        </eventsContext.Provider>
        </div>
    );
}
